import { Component } from '@angular/core';
import { Filme } from './filme.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  filmes: Filme[] = [
    {
      nome: this.filmes.entries[0].nome,
    }
  ];
  novoFilme: Filme = {
    nome: '',
    sinopse: '',
    assistido: false
  };

  adicionarFilme() {
    this.filmes.push(this.novoFilme);
    this.novoFilme = {
      nome: '',
      sinopse: '',
      assistido: false
    };
    this.ordenarFilmes();
  }

  removerFilme(filme: Filme) {
    const index = this.filmes.indexOf(filme);
    if (index > -1) {
      this.filmes.splice(index, 1);
    }
  }

  avaliarFilme(filme: Filme, avaliacao: boolean) {
    filme.avaliacao = avaliacao;
    this.ordenarFilmes();
  }

  adicionarComentario(filme: Filme, comentario: string) {
    filme.comentario = comentario;
  }

  ordenarFilmes() {
    this.filmes.sort((a, b) => {
      if (a.assistido && !b.assistido) {
        return 1;
      } else if (!a.assistido && b.assistido) {
        return -1;
      } else {
        return 0;
      }
    });

    const filmesAssistidos = this.filmes.filter(filme => filme.assistido);
    const filmesNaoAssistidos = this.filmes.filter(filme => !filme.assistido);
    this.filmes = [...filmesNaoAssistidos, ...filmesAssistidos];
  }

}





<h1>Meu Catálogo de Filmes</h1>

<h2>Adicionar Filme</h2>
<form (submit)="adicionarFilme()">
  <label for="nome">Nome:</label>
  <input type="text" id="nome" [(ngModel)]="novoFilme.nome" required>
  <br>
  <label for="sinopse">Sinopse:</label>
  <textarea id="sinopse" [(ngModel)]="novoFilme.sinopse" required></textarea>
  <br>
  <button type="submit">Adicionar</button>
</form>

<h2>Filmes</h2>
<ul>
  <li *ngFor="let filme of filmes" [ngClass]="{'assistido': filme.assistido}">
    <app-filme>{{filme.nome}}</app-filme>
    <h3>{{ filme.nome }}</h3>
    <p>{{ filme.sinopse }}</p>
    <div *ngIf="filme.avaliacao !== null">
      <p>Avaliação: {{ filme.avaliacao ? 'Gostou' : 'Não gostou' }}</p>
      <p>Comentário: {{ filme.comentario }}</p>
    </div>
    <button (click)="filme.assistido = !filme.assistido; ordenarFilmes()">{{ filme.assistido ? 'Desmarcar como assistido' : 'Marcar como assistido' }}</button>
    <button (click)="removerFilme(filme)">Remover</button>
    <div *ngIf="!filme.avaliacao">
      <p>Avaliação:
        <button (click)="avaliarFilme(filme, true)">Gostou</button>
        <button (click)="avaliarFilme(filme, false)">Não gostou</button>
      </p>
      <form (submit)="adicionarComentario(filme, comentario.value)">
        <input type="text" placeholder="Adicionar comentário" #comentario required>
        <button type="submit">Adicionar</button>
      </form>
    </div>
  </li>
</ul>
